function addItems(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t={title:n};$.post("todo/AddItem",t,function(){window.location="/todo"}).fail(function(n){if(n&&n.responseJSON){var t=n.responseJSON[Object.keys(n.responseJSON)[0]];$("#add-item-error").text(t).show()}})}$(document).ready(function(){$("#add-item-button").on("click",addItems)});